css:
.print-format td, .print-format th {
	padding: 1px !important;
	font-size:12px;
}

.print-format p {
    margin: 2px 0px 2px !important;
}

.print-format{
     margin-top:0px;
}

@page {
  margin: 1.5cm;
}

.header1{
    border:1px solid black;
    width:100%;
    height:230px;
}
.left-h{
    position: relative;
    width:55%;
    float:left;
    padding-left:10px;
    padding-top:20px;
    border-right:1px solid black;
    height:inherit;
}

.right-h{
    width:45%;
    float:right;
    height:inherit;
}
.tableheight{
    height:15px;
}
.header2{
    display:flex;
    border:1px solid black;
    width:100%;
    height:90px;
    border-top:0px solid black;
    border-bottom:0px solid black;
}
.header3{
    display:flex;
    border-left:1px solid black; 
    border-right:1px solid black; 
    width:100%;
    border-top:0px solid black;
    height:67px;
}
.left-h1{
    height:inherit;
    width:54.2%;
    float:left;
    padding-left:10px;
    border-right:1px solid black;
}

.right-h1{
    height:inherit;
    width:50%;
    float:right;
}
.items table tr td:first-child {
    border-left: 1px solid black;
}

.items table tr td:last-child {
    border-right: 1px solid black;
}

.below-items {
    border-left: 1px solid black;
    border-right: 1px solid black;
    border-bottom: 1px solid black;
}

.items{
    width:100%;
    border-top:0px solid black;
}
.words{
    width:100%;
    border-bottom:1px solid black;
}
.tax-words{
    width:100%;
    border-bottom:1px solid black;
}
.dec-left{
    width:50%;
    float:left;
    height:inherit;
}
.dec-right{
	width: 50%;
	float: right;
	border-left: 1px solid black;
	height: inherit;
	bottom: 0;
	display: flex;
	flex-direction: column;
	justify-content: space-between;
}

.print-header {
    width: 100%;
}

.print-header td {
    width: 33%;
    border: 1px solid black;
    border-bottom: 0;
    padding: 3px !important;
}

.print-header td:nth-last-child(-n+2) {
    width: 16.5%;
}

.items tbody .item-row td {
    border-bottom: 1px solid black;
}

.flex {
    display: flex;
    width: 100%;
}

.flex > div {
    width: 100%;
}

.flex > div:nth-child(-n+2) {
    padding-left: 10px;
}

.flex > div:last-child td {
    padding-left: 3px !important;
}

