{% if doc.customer_address %}
{% set ads_1_ba = frappe.db.get_value("Address",filters={'name':doc.customer_address},fieldname='address_line1') %}
{% set ads_2_ba = frappe.db.get_value("Address",filters={'name':doc.customer_address},fieldname='address_line2') %}
{% set city_ba = frappe.db.get_value("Address",filters={'name':doc.customer_address},fieldname='city') %}
{% set state_ba = frappe.db.get_value("Address",filters={'name':doc.customer_address},fieldname='state') %}
{% set pin_ba= frappe.db.get_value("Address",filters={'name':doc.customer_address},fieldname='pincode') %}
{% set coun_ba = frappe.db.get_value("Address",filters={'name':doc.customer_address},fieldname='country') %}
{% set gst_ba = frappe.db.get_value("Address",filters={'name':doc.customer_address},fieldname='gstin') %}
{% set state_code_ba = frappe.db.get_value("Address",filters={'name':doc.customer_address},fieldname='gst_state_number') %}
{% endif %}

{% if doc.shipping_address_name %}
{% set ads_1_sa = frappe.db.get_value("Address",filters={'name':doc.shipping_address_name},fieldname='address_line1') %}
{% set ads_2_sa = frappe.db.get_value("Address",filters={'name':doc.shipping_address_name},fieldname='address_line2') %}
{% set city_sa = frappe.db.get_value("Address",filters={'name':doc.shipping_address_name},fieldname='city') %}
{% set state_sa = frappe.db.get_value("Address",filters={'name':doc.shipping_address_name},fieldname='State') %}
{% set pin_sa = frappe.db.get_value("Address",filters={'name':doc.shipping_address_name},fieldname='Pincode') %}
{% set coun_sa = frappe.db.get_value("Address",filters={'name':doc.shipping_address_name},fieldname='Country') %}
{% set gst_sa = frappe.db.get_value("Address",filters={'name':doc.shipping_address_name},fieldname='gstin') %}
{% set state_code_sa = frappe.db.get_value("Address",filters={'name':doc.shipping_address_name},fieldname='gst_state_number') %}
{% endif %}

{% if doc.company_address %}
{% set ads_1_com = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='address_line1') %}
{% set ads_2_com= frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='address_line2') %}
{% set city = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='city') %}
{% set state = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='state') %}
{% set pin = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='pincode') %}
{% set coun = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='country') %}
{% set gst = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='gstin') %}
{% set state_code = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='gst_state_number') %}
{% set phone_com = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='phone') %}
{% set email_id_com = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='email_id') %}
{% endif %}

{% if doc.company %}
{% set com_contact1 = frappe.db.get_value("Company",filters={'name':doc.company},fieldname='phone_no') %}
{% set com_contact2 = frappe.db.get_value("Company",filters={'name':doc.company},fieldname='fax') %}
{% set com_email = frappe.db.get_value("Company",filters={'name':doc.company},fieldname='email') %}
{% endif %}

<html>
    <span ><b><center>Quotation</center></b></span>
    <div class="items">
        <table style="width:100%">
            <thead>
                <tr style='border-bottom:1px solid black;border-top:1px solid black;color:solid black;' class="table-header">
                    <td colspan="6" style="border-left: 1px solid black;">
                        <div style="padding: 5px; float: left; border-right: 1px solid black; width: 50%">
                            <p><b>{{doc.company.upper()}}</b></p> 
                            <p>Office&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : {{ads_1_com or ""}}</p>
                            <p style="padding-left: 70px">{{ads_2_com or ""}}</p>
                            <p style="padding-left: 70px">{{city or ""}} - {{pin or ""}}</p>
                        </div>
                        <div style=" padding: 5px; float: right; width: 50%">
                            <p style="">GSTIN/UIN &nbsp;&nbsp;: <b>{{gst or ""}}</b></p>
                            <p>Contact&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: <b>{{com_contact1}}, {{com_contact2}}</b></p>
                            <p>E-Mail&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: <b>{{com_email or ""}}</b></p>
                        </div>
                    </td>
                </tr>

                <tr style='border-bottom:1px solid black;border-top:1px solid black;color:solid black;' class="table-header">
                    <td colspan="6" style="border-left: 1px solid black; padding:0 !important">
                        <div class="flex">
                            <div style="padding-left:5px solid black !important; padding-right:5px solid black !important; border-right: 1px solid black; ">
                                <p><b>Bill To</b></p>
                                <p style="padding-left:8px">{{doc.customer_name}}</p>
                                {% if (ads_1_ba or ads_2_ba or city_ba or "") %}
                                    <p style="padding-left:8px">{{ads_1_ba or ""}} {% if (ads_2_ba and ads_1_ba) %}, {{ads_2_ba or ""}} {% endif %} {% if (city_ba and (ads_2_ba or ads_1_ba)) %}, {{city_ba or ""}} {% endif %}</p>
                                {% endif %}
                                {% if (gst_ba) %} <p>GSTIN/UIN: <b>{{gst_ba or ""}}</b></p> {% endif %}
                                {% if (state_ba) %} <p>Place of Supply : <b>{{state_ba or ""}}</b></p> {% endif %}
                                {% if (state_code_ba) %} <p>State Code : <b>{{state_code_ba or ""}}</b></p> {% endif %}
                            </div>
                            <div style="padding-left:5px solid black !important; padding-right:5px solid black !important;">
                                <p> <b>Ship To </b></p>
                                <p style="padding-left:8px">{{doc.customer_name}}</p>
                                {% if (ads_1_sa or ads_2_sa or city_sa or "") %}
                                    <p style="padding-left:8px">{{ads_1_sa or ""}} {% if (ads_2_sa and ads_1_sa) %}, {{ads_2_sa  or ""}} {% endif %} {% if (city_sa and (ads_1_sa or ads_2_sa)) %}, {{city_sa or ""}} {% endif %}</p>
                                {% endif %}
                                {% if (gst_sa) %} <p style="padding-left:10px;">GSTIN/UIN: <b>{{gst_sa or "" }}</b></p> {% endif %}
                                {% if (state_code_sa) %} <p>State Code : <b>{{state_code_sa or ""}}</b></p> {% endif %}
                            </div>
                            <div style="">
                                <table style="width: 100%">
                                     <tr>
                                        <td style="border-right:1px solid black;border-bottom:1px solid black">Quote No.</td>
                                        <td style="border-right:0; border-bottom:1px solid black">{{doc.name}}</td>
                                    </tr>
                                    <tr class="tableheight" >
                                        <td style="border-right:1px solid black;border-bottom:1px solid black">Dated</td>
                                        <td style="border-right:0; border-bottom:1px solid black">{{doc.get_formatted('transaction_date')}}</td>
                                    </tr>
                                    <tr class="tableheight" >
                                        <td style="border-right:1px solid black;border-bottom:1px solid black">Contact Person</td>
                                        <td style="border-right:0; border-bottom:1px solid black">{{doc.custom_ts_contact_person}}</td>
                                    </tr>

                                    <tr class="tableheight" >
                                        <td style="border-right:1px solid black;border-bottom:1px solid black">Contact Number</td>
                                        <td style="border-right:0; border-bottom:1px solid black">{{doc.custom_ts_contact_number}}</td>
                                    </tr>

                                    <tr class="tableheight" >
                                        <td style="border-right:1px solid black;border-bottom:1px solid black">Project Name</td>
                                        <td style="border-right:0; border-bottom:1px solid black">{{doc.custom_project_name}}</td>
                                    </tr>

                                    <tr class="tableheight" >
                                        <td style="border-right:1px solid black">Project Location</td>
                                        <td style="border-right:0;">{{doc.custom_project_location}}</td>
                                    </tr>
                                </table>
                            </div>
                        </div>
                    </td>
                </tr>

                <tr class="item-row" style='border-bottom:1px solid black;border-top:1px solid black;color:solid black; font-size:12px;'>
                    <td style="border-left:1px solid black; width:8%;"><center>SL No</center></td>
                    <td style="border-left:1px solid black;width:44"><center>Description of Goods/ Services</center></td>
                    <td style="width:13%;border-left:1px solid black;"><center>HSN/SAC</center></td>
                    <td style="width:10%;border-left:1px solid black;"><center>Quantity</center></td>
                    <td style="width:10%;border-left:1px solid black;"><center>Rate</center></td>
                    <td style="width:15%;border-left:1px solid black;"><center>Amount (Rs)</center></td>
                </tr>
            </thead>

             {% for row in doc.items %}
                <tr class="item-row">
                    <td style="width:8%; padding-left:20px; border-left:1px solid black"><b>{{row.idx}}</b></td>
                    <td style="border-left:1px solid black;width:44%">
                        <b>{{row.item_name.upper() }}</b>
                        <div>{{row.description}}</div>
                    </td>
                    <td style="width:13%;border-left:1px solid black;"><b>{{row.gst_hsn_code or ""}}</b></td>
                    <td style="width:10%;border-left:1px solid black;"><b>{{row.qty or 0}} {{row.uom or 0}}</b></td>
                    <td style="width:10%;border-left:1px solid black;"><b>{{"%.2f" % row.rate or 0.00}}</b></td>
                    <td style="width:15%;border-left:1px solid black;text-align:right"><b>{{"%.2f" % row.amount or 0.00}}</b></td> 
                 </tr>
             {% endfor %}
            {% if "In-state" in doc.taxes_and_charges %}
            {% if doc.taxes %}
            {% for tax in doc.taxes %}
                {% if "CGST" in tax.description %}
                    <tr style="">
                        <td style="border-left:1px solid black; width:8%;"></td>
                        <td style="border-left:1px solid black;text-align:right;width:44"><b><i>Output CGST@ {{tax.rate}}</i></b></td>
                        <td style="width:13%;border-left:1px solid black;"></td>
                        <td style="width:10%;border-left:1px solid black;"></td>
                        <td style="width:10%;border-left:1px solid black;"></td>
                        <td style="width:15%;border-left:1px solid black;text-align:right"><b>{{ "%.2f" % tax.tax_amount}}</b></td> 
                    </tr>
                {% endif %}
                {% if  "SGST" in tax.description %}
                    <tr style="">
                        <td style="border-left:1px solid black; width:8%;"></td>
                        <td style="border-left:1px solid black;text-align:right;width:44"><b><i> Output SGST@ {{tax.rate}}</i></b></td>
                        <td style="width:13%;border-left:1px solid black;"></td>
                        <td style="width:10%;border-left:1px solid black;"></td>
                        <td style="width:10%;border-left:1px solid black;"></td>
                        <td style="width:15%;border-left:1px solid black;text-align:right"><b>{{ "%.2f" % tax.tax_amount}}</b></td> 
                    </tr>
                {% endif %}

            {% endfor %}
            {% endif %}
            {% endif %}
            {% if "Out-state" in doc.taxes_and_charges %}
                {% if doc.taxes %}
                    {% for tax in doc.taxes %}
                        {% if "IGST" in tax.description %}
                            <tr style="">
                                <td style="border-left:1px solid black; width:8%;"></td>
                                <td style="border-left:1px solid black;text-align:right;width:44"><b><i>Output IGST@ {{tax.rate}}</i></b></td>
                                <td style="width:13%;border-left:1px solid black;"></td>
                                <td style="width:10%;border-left:1px solid black;"></td>
                                <td style="width:10%;border-left:1px solid black;"></td>
                                <td style="width:15%;border-left:1px solid black;text-align:right"><b>{{ "%.2f" % tax.tax_amount}}</b></td> 
                            </tr>
                        {% endif %}
                    {% endfor %}
                {% endif %}
            {% endif %} 
            <tr style="">
                <td style="border-left:1px solid black; width:8%;"></td>
                <td style="border-left:1px solid black;text-align:right;width:44"><b><i>Roundoff</i></b></td>
                <td style="width:13%;border-left:1px solid black;"></td>
                <td style="width:10%;border-left:1px solid black;"></td>
                <td style="width:10%;border-left:1px solid black;"></td>
                <td style="width:15%;border-left:1px solid black;text-align:right"><b>{{ doc.rounding_adjustment or ""}}</b></td> 
            </tr>
            <tr style="border-top:1px solid black; border-bottom:1px solid black">
                <td style="border-left:1px solid black; width:8%;"></td>
                <td style="border-left:1px solid black;text-align:right;width:44"><b>Total</b></td>
                <td style="width:13%;border-left:1px solid black;"></td>
                <td style="width:10%;border-left:1px solid black;"></td>
                <td style="width:10%;border-left:1px solid black;"></td>
                <td style="width:15%;border-left:1px solid black;text-align:right"><b>{{doc.get_formatted('grand_total')}}</b></td> 
             </tr>
        </table>
        <div class="below-items">
            <div class="words">
                <p style="margin: 0px; padding-top:7px; padding-bottom:7px; padding-left:10px">Amount Chargeable (in words) :   <b>{{doc.in_words}}</b></p>
            </div>
            <div class="tax-words">
                 {% set tax_amount = money_in_words(doc.total_taxes_and_charges) %}
                <p style="padding-left:10px">Tax Amount (in words)  :   <b>{{tax_amount}}</b></p>
            </div>
            <div class="declration" style="width:100%;height:3cm;">
               <div class="dec-left">
                   <p style= "padding-left:10px"><u>Declaration : </u></p>
               </div>
                <div class="dec-right">
                   <p style="text-align:right; padding-right:5px">For {{doc.company.upper()}}</p>
                   <p style="padding-left:10px; font-size:12px">Prepared by&nbsp;&nbsp;&nbsp;&nbsp;  Verified by&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  Authorised Signatory</p>
               </div>
            </div>
        </div>
    </div>
    <p><center>SUBJECT TO DURG JURISDICATION</center></p>
    <p><center>This is a Computer Generated Invoice</center></p>
</html>
