css:
.print-format td, .print-format th {
	padding: 1px !important;
	font-size:12px;
}
.print-format{
     margin-top:0px;
     /*margin-bottom:55px !important;*/
}
 @page {
      margin: 1.5cm;
    }
.header1{
    border:1px solid black;
    width:100%;
    height:300px;
}
.left-h{
    position: relative;
    width:55%;
    float:left;
    padding-left:10px;
    padding-top:20px;
    border-right:1px solid black;
    height:inherit;
}

.right-h{
    width:45%;
    float:right;
    height:inherit;
}
.tableheight{
    height:15px;
}
.header2{
    display:flex;
    border:1px solid black;
    width:100%;
    height:90px;
    border-top:0px solid black;
    border-bottom:0px solid black;
}
.header3{
    display:flex;
    border:1px solid black; 
    width:100%;
    border-top:0px solid black;
     height:67px;
}
.left-h1{
    height:inherit;
    width:50%;
    float:left;
    padding-left:10px;
    border-right:1px solid black;
}

.right-h1{
    height:inherit;
    width:50%;
    float:right;
}
.items{
    width:100%;
    border:1px solid Black;
    border-top:0px solid black;
}
.words{
    width:100%;
    border-bottom:1px solid black;
}
.tax-words{
    width:100%;
    border-bottom:1px solid black;
}
.dec-left{
    width:50%;
    float:left;
    height:inherit;
}
.dec-right{
	width: 50%;
	float: right;
	border-left: 1px solid black;
	height: inherit;
	bottom: 0;
	display: flex;
	flex-direction: column;
	justify-content: space-between;
}
